const lark = require\(''@larksuiteoapi/node-sdk''\);
const dotenv = require\(''dotenv''\);
dotenv.config\(\);

const client = new lark.Client\({
  appId: process.env.FEISHU_APP_ID,
  appSecret: process.env.FEISHU_APP_SECRET,
  appType: lark.AppType.SelfBuild,
  domain: lark.Domain.Feishu,
}\);

async function updateUser\(\) {
  const res = await client.bitable.appTableRecord.list\({
    path: {
      app_token: process.env.BITABLE_APP_TOKEN,
      table_id: process.env.BITABLE_TABLE_EMPLOYEES,
    },
  }\);

  if \(res.data?.items?.[0]\) {
    const recordId = res.data.items[0].record_id;
    console.log\(''更新记录:'', recordId\);

    const updateRes = await client.bitable.appTableRecord.update\({
      path: {
        app_token: process.env.BITABLE_APP_TOKEN,
        table_id: process.env.BITABLE_TABLE_EMPLOYEES,
        record_id: recordId,
      },
      data: {
        fields: {
          ''用户ID'': ''ou_24a540f8f75255feea5ae659406817a4'',
          ''姓名'': ''Gary'',
          ''邮箱'': ''gary@company.com'',
        },
      },
    }\);

    console.log\(''更新结果:'', updateRes.code === 0 ? ''成功'' : ''失败''\);
  }
}
updateUser\(\);
")

